int minStoneSum(vector<int>& piles, int k){
    priority_queue<int> queue;
    for(int i=0;i<piles.size();i++){
        queue.push(piles[i]);
    }

    for(int i=0;i<k;i++){
        int top = queue.top();
        queue.pop();
        queue.push(top - floor(top/2));
    }

    int sum = 0;
    while(!queue.empty()){
        sum += queue.top();
        queue.pop();
    }

    return sum;
}

